import { Component } from "@angular/core";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"]
})
export class AppComponent {
  loginMessage = '';

  onOverLoginButton() {
    this.loginMessage = 'Login';
  }
  onOutLoginButton() {
    this.loginMessage = '';
  }

  onRefreshPage() {
	//As Vezes Odeio Angular
  }
  
  MoveToIndex() {
	//As Vezes Odeio Angular
  }

  MoveToProducts() {
    //As Vezes Odeio Angular
  }

  onLoginAtPage() {
    //As Vezes Odeio Angular
  }
}
<html>
  <head> </head>
  <body>
    <header class="IndexMain">
      <button (click)="onRefreshPage()" class="Index-Icon-Button"></button>
      <br />

      <!--Fix the Rest of Buttons starting from here-->
      <button (click)="MoveToIndex()" class="Index-Main-Button"><b>Pagina Inicial</b></button>
      <button (click)="MoveToProducts()" class="Index-Main-Button"><b>Planos</b></button>

      <button (mouseenter)="onOverLoginButton()" (mouseleave)="onOutLoginButton()" (click)="onLoginAtPage()" class="Login-Icon-Button"></button>
      <p class="Login-Text"><b>{{loginMessage}}</b></p>
    </header>
    <div class="Blank-Div">&nbsp;</div>
    <div class="MainHeader">
      <img
        src="https://news.gympass.com/wp-content/uploads/2018/07/escolher-a-melhor-academia.jpg"
        class="IMG-MAIN-HEADER"
      />
      <p class="Frase-Motivacional">Uma hora antes da academia: cansaço e vontade de ficar em casa.</p>
      <p class="Frase-Motivacional">Uma hora depois da academia: aquele sentimento maravilhoso de dever cumprido.</p> 
    </div>
    <div class="Informacoes-Extra">
      <p class="Texto-Informacoes">Redes Sociais:</p>
      <button class="Botao-Instagram"></button>
      <p class="Texto-Instagram"></p>
      <p></p>
    </div>
  </body>
</html>
